<?php











namespace Composer;

use Composer\Semver\VersionParser;






class InstalledVersions
{
private static $installed = array (
  'root' => 
  array (
    'pretty_version' => '20210204.x-dev',
    'version' => '20210204.9999999.9999999.9999999-dev',
    'aliases' => 
    array (
    ),
    'reference' => '21c5c68b8201941b4ffa6df3374074c3468b25e0',
    'name' => '__root__',
  ),
  'versions' => 
  array (
    '__root__' => 
    array (
      'pretty_version' => '20210204.x-dev',
      'version' => '20210204.9999999.9999999.9999999-dev',
      'aliases' => 
      array (
      ),
      'reference' => '21c5c68b8201941b4ffa6df3374074c3468b25e0',
    ),
    'qiniu/php-sdk' => 
    array (
      'pretty_version' => 'v7.3.0',
      'version' => '7.3.0.0',
